  { title: "COMPARAÇÃO DOS MÉTODOS DE DOSE ADITIVA E DOSE REGENERATIVA NO PROCESSO DE DATAÇÃO POR TERMOLUMINESCÊNCIA", authors: ["Henry Lavalle Sullasi", "Renata Libonati Azevedo", "Anne-Marie Pessis", "Helen Jamil Khoury"], article_url: "https://periodicos.ufpe.br/revistas/index.php/clioarqueologica/article/view/246827", doi: nil, keywords: ["Datação", "Termoluminescência", "Dose acumulada"], abstract: "Nos sítios arqueológicos do Nordeste brasileiro é comum serem encontrados materiais cerâmicos e sedimentos, cuja datação é importante para a compreensão do desenvolvimento dos povos nesta região." },
  { title: "ENDOCANIBALISMO NA ÁREA ARQUEOLÓGICA DO SERIDÓ?", authors: ["Fábio Mafra Borges"], article_url: "https://periodicos.ufpe.br/revistas/index.php/clioarqueologica/article/view/246828", doi: nil, keywords: ["Arqueologia do Seridó", "Tarairiú", "Coprólitos", "Mioglobina", "Endocanibalismo"], abstract: "Neste artigo, apresentam-se os resultados da intervenção arqueológica no Sítio Furna do Umbuzeiro, Carnaúba dos Dantas - RN, Brasil. Descreve-se o sítio, os procedimentos utilizados na escavação e os problemas estratigráficos identificados no pacote sedimentar." },
  { title: "MEDIDAS LINEALES EN UN MUNDO ESFÉRICO: LA LEGUA ESPAÑOLA", authors: ["Carmen Rojas Sandoval"], article_url: "https://periodicos.ufpe.br/revistas/index.php/clioarqueologica/article/view/246829", doi: nil, keywords: ["Sistemas de medição", "Légua", "Navegação"], abstract: "Se revisan las medidas de navegación utilizadas en los siglos XV al XVIII, especialmente la legua española." },
